// Kiểm tra dữ liệu đầu vào
const inputData = items[0];
let htmlContent = "";

// Kiểm tra cấu trúc của dữ liệu đầu vào
if (inputData && typeof inputData === 'object') {
  // Nếu dữ liệu là một đối tượng, thử lấy thuộc tính 'data'
  if (inputData.data) {
    htmlContent = inputData.data;
  } else if (inputData.json && inputData.json.data) {
    // Thử lấy từ json.data nếu có
    htmlContent = inputData.json.data;
  } else {
    // Thử tìm bất kỳ thuộc tính nào chứa chuỗi HTML
    for (const key in inputData) {
      if (typeof inputData[key] === 'string' && 
          (inputData[key].includes('<div') || inputData[key].includes('<table'))) {
        htmlContent = inputData[key];
        break;
      }
    }
  }
} else if (typeof inputData === 'string') {
  // Nếu dữ liệu là một chuỗi, sử dụng trực tiếp
  htmlContent = inputData;
}

// Xử lý HTML nếu có
const result = [];
let updateTime = "";
let dataSource = "";

if (htmlContent && htmlContent.length > 0) {
  try {
    // Tìm tất cả các bảng trong HTML
    const tableRegex = /<table[^>]*>[\s\S]*?<tbody>[\s\S]*?<\/tbody>[\s\S]*?<\/table>/g;
    let tableMatch;

    while ((tableMatch = tableRegex.exec(htmlContent)) !== null) {
      const tableContent = tableMatch[0];
      const violation = {};

      // Tìm tất cả các hàng trong bảng
      const rowRegex = /<tr>[\s\S]*?<td>(.*?)<\/td>[\s\S]*?<td>([\s\S]*?)<\/td>[\s\S]*?<\/tr>/g;
      let rowMatch;

      while ((rowMatch = rowRegex.exec(tableContent)) !== null) {
        if (rowMatch.length >= 3) {
          const key = rowMatch[1].replace(':', '').trim();
          let value = rowMatch[2].trim();

          // Loại bỏ HTML tags từ giá trị
          value = value.replace(/<button.*?<\/button>/g, '');
          value = value.replace(/<[^>]*>/g, ' ').replace(/\s+/g, ' ').trim();

          // Chuyển đổi key thành camelCase
          const camelKey = key
            .normalize('NFD')
            .replace(/[\u0300-\u036f]/g, '')
            .replace(/đ/g, 'd')
            .replace(/Đ/g, 'D')
            .replace(/\s+(.)/g, (match, group) => group.toUpperCase())
            .replace(/\s/g, '')
            .replace(/^(.)/, (match, group) => group.toLowerCase());

          violation[camelKey] = value;
        }
      }

      // Chỉ thêm vi phạm nếu có ít nhất một trường dữ liệu
      if (Object.keys(violation).length > 0) {
        result.push(violation);
      }
    }

    // Trích xuất thời gian cập nhật
    const updateTimeMatch = htmlContent.match(/Dữ liệu được cập nhật vào lúc: ([\d:, -]+)/);
    if (updateTimeMatch && updateTimeMatch[1]) {
      updateTime = updateTimeMatch[1].trim();
    }

    // Trích xuất nguồn dữ liệu
    const sourceMatch = htmlContent.match(/Nguồn: <span[^>]*>(.*?)<\/span>/);
    if (sourceMatch && sourceMatch[1]) {
      dataSource = sourceMatch[1].trim();
    }

  } catch (error) {
    return {
      json: {
        error: true,
        message: `Lỗi khi xử lý dữ liệu: ${error.message}`
      }
    };
  }
}

// Trả về kết quả
return {
  json: {
    violations: result,
    totalViolations: result.length,
    licensePlate: result.length > 0 ? result[0].bienKiemSoat : '',
    vehicleType: result.length > 0 ? result[0].loaiPhuongTien : '',
    metadata: {
      updateTime: updateTime,
      dataSource: dataSource
    }
  }
};

