/**
 * Hàm tiện ích đa năng để làm sạch văn bản, bao gồm cả việc
 * giải mã các ký tự HTML đặc biệt (HTML entities).
 * @param {string} str - Chuỗi HTML đầu vào.
 * @returns {string} - Văn bản sạch.
 */
function cleanup(str) {
  if (!str) return '';
  try {
    // Bản đồ các ký tự HTML phổ biến
    const entities = {
        '&amp;': '&', '&lt;': '<', '&gt;': '>', '&quot;': '"', '&#39;': "'",
        '&nbsp;': ' ', '&agrave;': 'à', '&Agrave;': 'À', '&aacute;': 'á', '&Aacute;': 'Á',
        '&acirc;': 'â', '&Acirc;': 'Â', '&atilde;': 'ã', '&Atilde;': 'Ã', '&egrave;': 'è',
        '&Egrave;': 'È', '&eacute;': 'é', '&Eacute;': 'É', '&ecirc;': 'ê', '&Ecirc;': 'Ê',
        '&igrave;': 'ì', '&Igrave;': 'Ì', '&iacute;': 'í', '&Iacute;': 'Í', '&ograve;': 'ò',
        '&Ograve;': 'Ò', '&oacute;': 'ó', '&Oacute;': 'Ó', '&ocirc;': 'ô', '&Ocirc;': 'Ô',
        '&otilde;': 'õ', '&Otilde;': 'Õ', '&ugrave;': 'ù', '&Ugrave;': 'Ù', '&uacute;': 'ú',
        '&Uacute;': 'Ú', '&yacute;': 'ý', '&Yacute;': 'Ý', '&đ;': 'đ', '&Đ;': 'Đ'
    };

    // Thay thế các ký tự HTML
    let decodedStr = str.replace(/&[a-zA-Z#0-9]+;/g, entity => entities[entity] || entity);

    // Dọn dẹp các thẻ và khoảng trắng
    return decodedStr
      .replace(/<br\s*\/?>/gi, '\n')
      .replace(/<p[^>]*>/gi, '\n')
      .replace(/<li[^>]*>/gi, '\n- ')
      .replace(/<[^>]*>?/g, ' ')
      .replace(/“|”/g, '"')
      .replace(/[ \t]+/g, ' ')
      .replace(/\n\s*\n/g, '\n')
      .replace(/^[\s\n]+|[\s\n]+$/g, '')
      .trim();
  } catch (e) {
    return str;
  }
}


/**
 * Hàm trích xuất nội dung dựa vào một nhãn (label) cụ thể.
