    const regex = new RegExp(`<label>${labelText}\\s*<\\/label>[\\s\\S]*?<\\/td>\\s*<td>([\\s\\S]*?)<\\/td>`, 'i');
    const match = html.match(regex);
    return cleanup(match?.[1] || '');
